import math

def convert_to_message(start, end):
    xi, yi = vertices[start[0]][start[1]]
    xf, yf = vertices[end[0]][end[1]]

    deltax = xf - xi
    deltay = yf - yi
    cosine = (deltax)/math.sqrt(deltax**2 + deltay**2)
    theta = math.acos(cosine)

    if deltay < 0:
        theta = -theta
    
    for i in packet['data']:
        if i['name'] == start[0]:
            thetac = i['angle']
    
    word = math.degrees(theta - thetac)
    word = round(word)

    return word

